<template>
  <q-img
    :class="['player-img', { rounded }]"
    v-bind="props"
    :placeholder-src="placeholder"
    v-on="$listeners"
  />
</template>

<script>
import QImg from '../quasar/Img'

export default {
  name: 'PlayerImg',

  extends: QImg,

  components: {
    QImg
  },

  props: {
    rounded: Boolean
  },

  computed: {
    props () {
      const { rounded, ...props } = this.$props
      return props
    },
    placeholder () {
      const light = 'data:image/svg+xml;base64,'
      const dark = 'data:image/svg+xml;base64,'

      return this.$q.dark.isActive ? dark : light
    }
  }
}
</script>

<style lang="scss" scoped>
.player-img {
  &.rounded {
    border-radius: 50%;

    /deep/ .q-img__image {
      background-size: 85% !important;
      background-position: 50% 100% !important;
    }
  }
}
</style>
