<template>
  <q-img
    class="team-logo"
    v-bind="$props"
    :placeholder-src="placeholder"
    v-on="$listeners"
  >
    <slot>
      <q-tooltip>
        <q-item-label v-text="name" />
      </q-tooltip>
    </slot>
  </q-img>
</template>

<script>
import QImg from '../quasar/Img'
import { pascalCase } from '../../utils/string'
import { QTooltip, QItemLabel } from 'quasar'

export default {
  name: 'TeamLogo',

  extends: QImg,

  components: {
    QImg,
    QItemLabel,
    QTooltip
  },

  computed: {
    placeholder () {
      const light = 'data:image/svg+xml;base64,'
      const dark = 'data:image/svg+xml;base64,'

      return this.$q.dark.isActive ? dark : light
    },

    name: vm => pascalCase(vm.item?.name)
  }
}
</script>
